installation.script:
#!/bin/bash

# Directory setup
WEB_ROOT="/mnt/server"
mkdir -p "$WEB_ROOT" && chmod 755 "$WEB_ROOT"

# Remove existing index files
echo "ðŸ—‘ï¸ Removing existing index files..."
rm -f "$WEB_ROOT/index.php" "$WEB_ROOT/index.html"

# Get the selected software stack
STACK_TYPE="${SOFTWARE}"

# Create appropriate index file based on stack type
case "$STACK_TYPE" in
    php*|WordPress)
        INDEX_FILE="$WEB_ROOT/index.php"
        cat > "$INDEX_FILE" <<EOL
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link rel="icon" type="image/x-icon" href="https://cdn.flyxnodes.xyz/cdn/misc/favicon.ico">
    <title>Welcome to FlyxWeb - PHP Server</title>
    <style>
        :root {
            --primary-color: #4287f5;
            --secondary-color: #a8a8a8;
            --accent-color: #ff7b00;
            --text-color: #fff;
            --bg-dark: #121212;
            --card-bg: rgba(32, 32, 32, 0.8);
        }

        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: var(--text-color);
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            text-align: center;
        }

        html {
            background-color: var(--bg-dark);
            background-image: 
                radial-gradient(circle at 25% 25%, rgba(66, 135, 245, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 75% 75%, rgba(255, 123, 0, 0.1) 0%, transparent 50%);
        }

        .container {
            background: var(--card-bg);
            padding: 2.5rem;
            border-radius: 12px;
            box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
            max-width: 700px;
            width: 90%;
            backdrop-filter: blur(10px);
            border: 1px solid rgba(255, 255, 255, 0.1);
        }

        h1 {
            color: var(--primary-color);
            margin: 0 0 1.5rem 0;
            font-size: 2.5rem;
            font-weight: 700;
        }

        .logo {
            font-size: 3rem;
            margin-bottom: 1rem;
            color: var(--accent-color);
        }

        p {
            font-size: 1.1rem;
            margin: 1rem 0;
            line-height: 1.6;
        }

        .info {
            background: rgba(66, 135, 245, 0.1);
            border-left: 4px solid var(--primary-color);
            padding: 1rem;
            margin: 1.5rem 0;
            text-align: left;
            border-radius: 0 4px 4px 0;
        }

        .notice {
            margin-top: 2rem;
            font-size: 0.9rem;
            color: var(--secondary-color);
            padding-top: 1rem;
            border-top: 1px solid rgba(255, 255, 255, 0.1);
        }

        code {
            background: rgba(0, 0, 0, 0.3);
            padding: 0.2rem 0.4rem;
            border-radius: 4px;
            font-family: 'Courier New', monospace;
            font-size: 0.9em;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="logo">âš¡</div>
        <h1>Welcome to FlyxWeb</h1>
        <p>Your PHP web server is up and running!</p>

        <div class="info">
            <p><strong>Server Information:</strong></p>
            <p>PHP Version: <code><?php echo phpversion(); ?></code></p>
            <p>Server Time: <code><?php echo date('Y-m-d H:i:s'); ?></code></p>
            <p>Server Software: <code><?php echo \$_SERVER['SERVER_SOFTWARE'] ?? 'FlyxWeb'; ?></code></p>
            <p>Document Root: <code><?php echo \$_SERVER['DOCUMENT_ROOT']; ?></code></p>
        </div>

        <p class="notice">This is a default page. Replace or modify this file to build your website.</p>
    </div>
</body>
</html>
EOL
        ;;

    python|vercel-serve|dotnet*|astro|nextjs)
        INDEX_FILE="$WEB_ROOT/index.html"
        cat > "$INDEX_FILE" <<EOL
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link rel="icon" type="image/x-icon" href="https://cdn.flyxnodes.xyz/cdn/misc/favicon.ico">
    <title>Welcome to FlyxWeb - Web Server</title>
    <style>
        :root {
            --primary-color: #4287f5;
            --secondary-color: #a8a8a8;
            --accent-color: #ff7b00;
            --text-color: #fff;
            --bg-dark: #121212;
            --card-bg: rgba(32, 32, 32, 0.8);
        }

        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: var(--text-color);
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            text-align: center;
        }

        html {
            background-color: var(--bg-dark);
            background-image: 
                radial-gradient(circle at 25% 25%, rgba(66, 135, 245, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 75% 75%, rgba(255, 123, 0, 0.1) 0%, transparent 50%);
        }

        .container {
            background: var(--card-bg);
            padding: 2.5rem;
            border-radius: 12px;
            box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
            max-width: 700px;
            width: 90%;
            backdrop-filter: blur(10px);
            border: 1px solid rgba(255, 255, 255, 0.1);
        }

        h1 {
            color: var(--primary-color);
            margin: 0 0 1.5rem 0;
            font-size: 2.5rem;
            font-weight: 700;
        }

        .logo {
            font-size: 3rem;
            margin-bottom: 1rem;
            color: var(--accent-color);
        }

        p {
            font-size: 1.1rem;
            margin: 1rem 0;
            line-height: 1.6;
        }

        .info {
            background: rgba(66, 135, 245, 0.1);
            border-left: 4px solid var(--primary-color);
            padding: 1rem;
            margin: 1.5rem 0;
            text-align: left;
            border-radius: 0 4px 4px 0;
        }

        .notice {
            margin-top: 2rem;
            font-size: 0.9rem;
            color: var(--secondary-color);
            padding-top: 1rem;
            border-top: 1px solid rgba(255, 255, 255, 0.1);
        }

        code {
            background: rgba(0, 0, 0, 0.3);
            padding: 0.2rem 0.4rem;
            border-radius: 4px;
            font-family: 'Courier New', monospace;
            font-size: 0.9em;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="logo">âš¡</div>
        <h1>Welcome to FlyxWeb</h1>
        <p>Your web server is up and running!</p>

        <div class="info">
            <p><strong>Server Information:</strong></p>
            <p>Technology: <code>$STACK_TYPE</code></p>
        </div>

        <p class="notice">This is a default page. Replace or modify this file to build your website.</p>
    </div>
</body>
</html>
EOL
        ;;

    *)
        echo "âš ï¸ Unrecognized stack: '$STACK_TYPE'" >&2
        exit 1
        ;;
esac

# Check if the index file was created properly
if [ -f "$INDEX_FILE" ]; then
    chmod 644 "$INDEX_FILE"
    echo "âœ… File created successfully: $INDEX_FILE"
else
    echo "âŒ Critical error: Failed to create $INDEX_FILE" >&2
    exit 1
fi

# Create configuration directory
CONFIG_DIR="$WEB_ROOT/.flyxweb"
mkdir -p "$CONFIG_DIR"
chmod 755 "$CONFIG_DIR"

# Create database directory for SQLite if needed
if [ "${DB_ENGINE}" = "sqlite" ]; then
    DB_DIR="$WEB_ROOT/.flyxweb/database"
    mkdir -p "$DB_DIR"
    chmod 755 "$DB_DIR"
    echo "âœ… SQLite database directory created"
fi

# Create a status file to indicate installation success
echo "{\"installed\": true, \"timestamp\": \"$(date -u +"%Y-%m-%dT%H:%M:%SZ")\", \"stack\": \"$STACK_TYPE\"}" > "$CONFIG_DIR/status.json"

echo "âœ… FlyxWeb installation completed successfully!"
